import { useEffect } from "react";


const ScrollToTop = ({ children, location: { pathname } }) => {
  useEffect(() => {
    window.scrollTo({
      top: 0,
      left: 0,
      behavior: "smooth"
    });
  }, [pathname]);
  return children || null;
};
const ScrollToMiddle = ({ children, location: { pathname } }) => {
  useEffect(() => {
    window.scrollTo({
      top: 100,
      left: 50,
      behavior: "smooth"
    });
  }, [pathname]);
  return children || null;
};

export { ScrollToTop, ScrollToMiddle }
